{

draw((0,i)--(3,i),linewidth(1));
}

for(int j = 0; j<4; ++j)
{

draw((j,0)--(j,3),linewidth(1));
}

label("$n-3$",(.5,.5));
label("3",(.5,1.5));
label("$n+1$",(.5,2.5));

